public class Part3 {
    String string = "";
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int foundIndex = dna.indexOf(stopCodon, startIndex+3);
        //System.out.println(foundIndex);
        while (foundIndex!=-1){
            int diff = foundIndex - startIndex;
            if(diff%3==0)
                return foundIndex;
            else
                foundIndex = dna.indexOf(stopCodon, foundIndex+1);
        }
        return -1;
    }
    public void testFindStopCodon(){
        System.out.println(findStopCodon(string, 0, "TGA"));
    }
    public String findGene(String dna, int si){
        int startIndex = dna.indexOf("ATG", si);
        //System.out.println(startIndex);
        if(startIndex == -1)
            return "";
        int indexTAA = findStopCodon(dna, startIndex, "TAA");
        int indexTAG = findStopCodon(dna, startIndex, "TAG");
        int indexTGA = findStopCodon(dna, startIndex, "TGA");
        //int min = Math.min(indexTAA, Math.min(indexTGA, indexTAG));
        //System.out.println(min);
        int min = 0;
        if(indexTAA == -1 || (indexTGA != -1 && indexTGA < indexTAA))
            min = indexTGA;
        else
            min = indexTAA;
        if(min == -1 || (indexTAG != -1 && indexTAG < min))
            min = indexTAG;
        if(min == -1)
            return "";
        return dna.substring(startIndex, min+3);
    }
    public int printAllGenes(String dna){
        int geneCount = 0;
        int startIndex = 0;
        while(true){
            String currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()){
                break;
            }
            System.out.println(currentGene);
            geneCount++;
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return geneCount;
    }
    public int countGenes(String dna){
        int geneCount = printAllGenes(dna);
        return geneCount;
    }
    public void testCountGene(){
        System.out.println(countGenes(string));
    }
    public static void main(String[] args) {
        Part3 p = new Part3();
        p.testCountGene();
    }
}
