import edu.duke.*;

public class Part1 {
    String string = "";
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int foundIndex = dna.indexOf(stopCodon, startIndex+3);
        //System.out.println(foundIndex);
        while (foundIndex!=-1){
            int diff = foundIndex - startIndex;
            if(diff%3==0)
                return foundIndex;
            else
                foundIndex = dna.indexOf(stopCodon, foundIndex+1);
        }
        return -1;
    }
    public void testFindStopCodon(){
        System.out.println(findStopCodon(string, 0, "TGA"));
    }
    public String findGene(String dna, int si){
        int startIndex = dna.indexOf("ATG", si);
        //System.out.println(startIndex);
        if(startIndex == -1)
            return "";
        int indexTAA = findStopCodon(dna, startIndex, "TAA");
        int indexTAG = findStopCodon(dna, startIndex, "TAG");
        int indexTGA = findStopCodon(dna, startIndex, "TGA");
        //int min = Math.min(indexTAA, Math.min(indexTGA, indexTAG));
        //System.out.println(min);
        int min = 0;
        if(indexTAA == -1 || (indexTGA != -1 && indexTGA < indexTAA))
            min = indexTGA;
        else
            min = indexTAA;
        if(min == -1 || (indexTAG != -1 && indexTAG < min))
            min = indexTAG;
        if(min == -1)
            return "";
        return dna.substring(startIndex, min+3);
    }
    public void testFindGene(){
        System.out.println(findGene("ATGJHJHJHTAAHJHTGAJLM",0));
    }

    public StorageResource getAllGenes(String dna){
        StorageResource storageResource = new StorageResource();
        int startIndex = 0;
        while(true){
            String currentGene = findGene(dna, startIndex);
            System.out.println(currentGene);
            if (currentGene.isEmpty()){
                break;
            }
            storageResource.add(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return storageResource;
    }
    public int howMany(String stringa, String stringb){
        int count = 0;
        int startIndex = stringb.indexOf(stringa);
        while(startIndex != -1){
            count++;
            startIndex = stringb.indexOf(stringa, startIndex+stringa.length());
        }
        return count;
    }
    public  double cgRatio(String dna){
        return ((double)(howMany("C", dna)+howMany("G",dna))/dna.length());
    }
    public int countCTG(String dna){
        return howMany("CTG",dna);
    }
    public void processGenes(StorageResource sr){
        int stringCount = 0;
        int stringCgCount = 0;
        int maxLength = 0;
        System.out.println("Strings with length greater than 60:");
        for (String string: sr.data()){
            if(maxLength < string.length())
                maxLength = string.length();
            if(string.length()>60){
                System.out.println(string);
                stringCount++;
            }
        }
        System.out.println("Strings with C/G ratio greater than 0.35:");
        for (String string: sr.data()){
            if(cgRatio(string)>0.35){
                System.out.println(string);
                stringCgCount++;
            }
        }
        System.out.println("Number of strings with length greater than 60:" + stringCount);
        System.out.println("Number of strings with C/G ratio greater than 0.35:" + stringCgCount);
    }
    public static void main(String[] args) {
        FileResource fileResource = new FileResource("files/brca1line.fa");
        String str = fileResource.asString().toUpperCase();
        //System.out.println(str);
        Part1 p = new Part1();
//        p.testFindStopCodon();
//        p.testFindGene();
        StorageResource storageResource = p.getAllGenes(str);
        for(String string : storageResource.data()){
            System.out.println(string);
        }
        p.processGenes(storageResource);
    }
}
